import React, { useState } from 'react';
import TodoItem from './TodoItem';
import InputArea from './InputArea';

function App() {
  const [items, setItems] = useState([]);

  function handleClick(inputText) {
    setItems((prevItems) => [...prevItems, { text: inputText, isEditing: false }]);
  }

  function handleEdit(id, editedText) {
    setItems((prevItems) =>
      prevItems.map((item, index) => {
        if (index === id) {
          item.text = editedText;
          item.isEditing = false;
        }
        return item;
      })
    );
  }

  function handleDelete(id) {
    setItems((prevItems) => prevItems.filter((item, index) => index !== id));
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <InputArea onAdd={handleClick} />
      <div>
        <ul>
          {items.map((todoItem, index) => (
            <TodoItem
              key={index}
              id={index}
              text={todoItem.text}
              isEditing={todoItem.isEditing}
              onChecked={() => handleEdit(index, todoItem.text)}
              onEdit={handleEdit}
              onDeleted={handleDelete}
            />
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
