import React, { useState } from "react";

function App() {
  const [inputText, setInputText] = useState("");
  const [items, setItems] = useState([]);

  function handleChange(event) {
    const newValue = event.target.value;
    setInputText(newValue);
  }

  function handleClick(event) {
    // Clear the input box after adding a todo
    setInputText("");

    setItems((prevItems) => {
      return [...prevItems, inputText];
    });
  }

  function handleDelete(index) {
    // Use the index to remove the item from the array
    setItems((prevItems) => {
      const updatedItems = [...prevItems];
      updatedItems.splice(index, 1);
      return updatedItems;
    });
  }

  function handleEdit(index, newValue) {
    // Prompt the user for a new value, and update the item in the array
    const updatedValue = prompt("Edit todo item:", newValue);
    if (updatedValue !== null) {
      setItems((prevItems) => {
        const updatedItems = [...prevItems];
        updatedItems[index] = updatedValue;
        return updatedItems;
      });
    }
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={handleChange} type="text" value={inputText} />
        <button onClick={handleClick}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {items.map((todoItem, index) => (
            <li key={index}>
              {todoItem}
              <button onClick={() => handleEdit(index, todoItem)}>Edit</button>
              <button onClick={() => handleDelete(index)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
