import React, { useState } from "react";
import TodoItem from "./TodoItem";
import InputArea from "./InputArea";

function App() {
  const [items, setItems] = useState([]);

  function handleClick(inputText) {
    setItems((prevItems) => {
      return [...prevItems, { text: inputText, isEditing: false }];
    });
  }

  function toggleEdit(index) {
    setItems((prevItems) => {
      const updatedItems = [...prevItems];
      updatedItems[index].isEditing = !updatedItems[index].isEditing;
      return updatedItems;
    });
  }

  function editItem(index, newText) {
    setItems((prevItems) => {
      const updatedItems = [...prevItems];
      updatedItems[index].text = newText;
      return updatedItems;
    });
  }

  function deleteItem(index) {
    setItems((prevItems) => {
      return prevItems.filter((item, i) => i !== index);
    });
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <InputArea onAdd={handleClick} />
      <div>
        <ul>
          {items.map((todoItem, index) => (
            <TodoItem
              key={index}
              id={index}
              text={todoItem.text}
              isEditing={todoItem.isEditing}
              onChecked={() => deleteItem(index)}
              onEdit={() => toggleEdit(index)}
              onEditSave={(newText) => editItem(index, newText)}
            />
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
